package bus.uigen.widgets.swing;//import javax.swing.Icon;import javax.swing.JProgressBar;import bus.uigen.widgets.ProgressBarFactory;import bus.uigen.widgets.VirtualProgressBar;//import javax.swing.JSlider;//import javax.swing.JPanel;//import bus.uigen.widgets.SliderFactory;//import bus.uigen.widgets.VirtualSlider;//import java.awt.Container;//import java.awt.Color;public class SwingProgressBarFactory implements ProgressBarFactory {	static int id;	public VirtualProgressBar createProgressBar() {		/*		 * Container panel = new JPanel(); panel.setName("" + getNewID());		 * //panel.setBackground(Color.white); return panel; //return new		 * Panel(); //return new JPanel();		 */		return createJProgressBar();	}	public VirtualProgressBar createProgressBar(int val) {		/*		 * Container panel = new JPanel(); panel.setName("" + getNewID());		 * //panel.setBackground(Color.white); return panel; //return new		 * Panel(); //return new JPanel();		 */		return createJProgressBar(val);	}	public VirtualProgressBar createProgressBar(int val, int min, int max) {		return createJProgressBar(val, min, max);	}	static int getNewID() {		return id++;	}	public static SwingProgressBar createJProgressBar(int value) {		JProgressBar progressBar = new JProgressBar(value);		return SwingProgressBar.virtualProgressBar(progressBar);		// return new Panel();		// return new JPanel();	}	public static SwingProgressBar createJProgressBar(int value, int min,			int max) {		JProgressBar progressBar = new JProgressBar(value, min, max);		SwingProgressBar toReturn = SwingProgressBar				.virtualProgressBar(progressBar);		toReturn.init();		return toReturn;		// return new Panel();		// return new JPanel();	}	public static SwingProgressBar createJProgressBar() {		JProgressBar progressBar = new JProgressBar();		SwingProgressBar toReturn = SwingProgressBar				.virtualProgressBar(progressBar);		toReturn.init();		return toReturn;		// return new Panel();		// return new JPanel();	}}