package bus.uigen.widgets.awt;//import javax.swing.Icon;//import javax.swing.JButton;import java.awt.PopupMenu;import java.util.Hashtable;import javax.swing.JMenu;import bus.uigen.widgets.PopupMenuFactory;import bus.uigen.widgets.VirtualPopupMenu;//import javax.swing.JPanel;//import javax.swing.JPopupMenu;//import bus.uigen.widgets.MenuFactory;//import bus.uigen.widgets.VirtualMenu;//import java.awt.Container;//import java.awt.Color;//import java.awt.Menu;public class AWTPopupMenuFactory implements PopupMenuFactory {	static int id;	public VirtualPopupMenu createMenu(String text) {		/*		 * Container panel = new JPanel(); panel.setName("" + getNewID());		 * //panel.setBackground(Color.white); return panel; //return new		 * Panel(); //return new JPanel();		 */		return createAWTPopupMenu(text);	}	/*	 * public VirtualMenu createMenu (Object icon) {	 * 	 * return createMenu((Icon) icon); }	 */	public VirtualPopupMenu createMenu() {		/*		 * Container panel = new JPanel(); panel.setName("" + getNewID());		 * //panel.setBackground(Color.white); return panel; //return new		 * Panel(); //return new JPanel();		 */		return createAWTMenu();	}	static int getNewID() {		return id++;	}	public static AWTPopupMenu createAWTPopupMenu(String text) {		PopupMenu menuItem = new PopupMenu(text);		return virtualPopupMenu(menuItem);		// return new Panel();		// return new JPanel();	}	/*	 * public static ASwingMenu createSwingMenu (Icon icon) { Menu menuItem =	 * new Menu(icon); return virtualMenu(menuItem); //return new Panel();	 * //return new JPanel(); }	 */	public static AWTPopupMenu createAWTMenu() {		PopupMenu menuItem = new PopupMenu();		return virtualPopupMenu(menuItem);		// return new Panel();		// return new JPanel();	}	static transient Hashtable<JMenu, AWTMenu> MenusToVirtualMenus = new Hashtable<JMenu, AWTMenu>();	public static AWTPopupMenu virtualPopupMenu(PopupMenu theMenu) {		return AWTPopupMenu.virtualPopupMenu