package bus.uigen.widgets.swing;//import javax.swing.Icon;//import javax.swing.JButton;import java.util.Hashtable;import javax.swing.JMenu;import javax.swing.JPopupMenu;import bus.uigen.widgets.PopupMenuFactory;import bus.uigen.widgets.VirtualPopupMenu;public class SwingPopupMenuFactory implements PopupMenuFactory {	static int id;	public VirtualPopupMenu createMenu(String text) {		/*		 * Container panel = new JPanel(); panel.setName("" + getNewID());		 * //panel.setBackground(Color.white); return panel; //return new		 * Panel(); //return new JPanel();		 */		return createSwingPopupMenu(text);	}	/*	 * public VirtualMenu createMenu (Object icon) {	 * 	 * return createMenu((Icon) icon); }	 */	public VirtualPopupMenu createMenu() {		/*		 * Container panel = new JPanel(); panel.setName("" + getNewID());		 * //panel.setBackground(Color.white); return panel; //return new		 * Panel(); //return new JPanel();		 */		return createSwingMenu();	}	static int getNewID() {		return id++;	}	public static SwingPopupMenu createSwingPopupMenu(String text) {		JPopupMenu menuItem = new JPopupMenu(text);		return virtualPopupMenu(menuItem);		// return new Panel();		// return new JPanel();	}	/*	 * public static ASwingMenu createSwingMenu (Icon icon) { Menu menuItem =	 * new Menu(icon); return virtualMenu(menuItem); //return new Panel();	 * //return new JPanel(); }	 */	public static SwingPopupMenu createSwingMenu() {		JPopupMenu menuItem = new JPopupMenu();		return virtualPopupMenu(menuItem);		// return new Panel();		// return new JPanel();	}	static transient Hashtable<JMenu, SwingMenu> MenusToVirtualMenus = new Hashtable<JMenu, SwingMenu>();	public static SwingPopupMenu virtualPopupMenu(JPopupMenu theMenu) {		return SwingPopupMenu.virtualPopupMenu(theMenu);	}}